queries.query:
SELECT ?subEvent ?location WHERE {
  ?subEventId conference:isSubEventOf <https://metadata.2019.eswc-conferences.org/rdf/ontology/Conference>;
              rdfs:label ?subEvent;
              conference:hasLocation [
			      conference:description ?location
  			  ].
}

SELECT ?title ?track WHERE {
  ?submission a eswc2020:SubmissionsPaper;
    conference:title ?title;
    eswc2020:track [ rdfs:label ?track ].
}

SELECT DISTINCT ?authorName WHERE {
  ?author conference:name ?authorName;
          conference:holdsRole [ conference:withRole conference:PublishingRole ].
}
